#
# 1) Update Definitions table with new UI for Scope CRUD
#

LOCK TABLES `appbuilder_definition` WRITE;

ON DUPLICATE KEY UPDATE
    name = VALUES(name),
    type = VALUES(type),
    json = VALUES(json),
    createdAt = VALUES(createdAt),
    updatedAt = VALUES(updatedAt);

UNLOCK TABLES;
